import { Component, OnInit } from '@angular/core';
import { Subscription } from 'rxjs';
import { CartService } from '../Services/cart.service';

@Component({
  selector: 'app-nav-menu',
  templateUrl: './nav-menu.component.html',
  styleUrls: ['./nav-menu.component.css']
})
export class NavMenuComponent implements OnInit {
  cartService!: CartService; // Mark the property with definite assignment assertion
  cartItemCount: number = 0;

  private cartItemsSubscription: Subscription = new Subscription;
  constructor(cartService: CartService) {
    this.cartService = cartService; // Initialize the property in the constructor
  }
  isExpanded = false;

  collapse() {
    this.isExpanded = false;
  }

  toggle() {
    this.isExpanded = !this.isExpanded;
  }
  ngOnInit(): void {
    this.updateCartCount();

    // Subscribe to changes in the cart items and update the count
    this.cartService.cartItemsChanged.subscribe(() => {
      this.updateCartCount();
    });
  }
  private updateCartCount(): void {
    this.cartService.getCartItems().subscribe(cartItems => {
      this.cartItemCount = cartItems.reduce((total, item) => total + item.quantity, 0);
    });
  }
}
<!-- item-list-page.component.html -->
<header>
  <nav class="navbar navbar-expand-sm navbar-toggleable-sm navbar-light bg-white border-bottom box-shadow mb-3">
    <div class="container">
      <a class="navbar-brand" [routerLink]="['/']">ED.Web</a>
      <button class="navbar-toggler"
              type="button"
              data-toggle="collapse"
              data-target=".navbar-collapse"
              aria-label="Toggle navigation"
              [attr.aria-expanded]="isExpanded"
              (click)="toggle()">
        <span class="navbar-toggler-icon"></span>
      </button>
      <div class="navbar-collapse collapse d-sm-inline-flex justify-content-end" [ngClass]="{ show: isExpanded }">
        <ul class="navbar-nav flex-grow">
          <li class="nav-item"
              [routerLinkActive]="['link-active']"
              [routerLinkActiveOptions]="{ exact: true }">
            <a class="nav-link text-dark" [routerLink]="['/']">Home</a>
          </li>
          <li>
            <a routerLink="/cart">
              <i class="fa fa-shopping-cart"></i>
              <span class="cart-counter" *ngIf="cartItemCount > 0">{{ cartItemCount }}</span>
            </a>
          </li>
        </ul>
      </div>
    </div>
  </nav>
</header>
